$(document).ready(function(){$.fn.delfinidialog=function(){$(this).animate({top:"0px"})};var elements=$("input:text, input:password, input[type=email]");elements.wrap('<div class="inputWrapper" />').after(function(){var a=$(this);if(a.attr("type").toLowerCase()=="password"){return'<span class="passwordPeak" title="Press and hold to view this password" />'}else{return'<span class="inputClear" title="Click to clear contents" />'}});elements.siblings("span").mousedown(function(){var b=$(this);var a=b.siblings("input");if(a.attr("type").toLowerCase()=="password"){var c=a.attr("value");a.hide();$('<input class="passwordViewer" type="text" />').attr("value",c).insertAfter(b)}else{a.val("")}});elements.siblings("span").mouseup(function(){var b=$(this);var a=b.siblings("input");if(a.attr("type").toLowerCase()=="password"){b.siblings("input.passwordViewer").remove();a.show()}})});