$(document).ready(function(){var a=$("input:text, input:password, input[type=email]");a.wrap('<div class="inputWrapper" />').after(function(){var b=$(this);if(b.attr("type").toLowerCase()=="password"){return'<span class="passwordPeak" title="Press and hold to view this password" />'}else{return'<span class="inputClear" title="Click to clear contents" />'}});a.siblings("span").mousedown(function(){var c=$(this);var b=c.siblings("input");if(b.attr("type").toLowerCase()=="password"){var d=b.attr("value");b.hide();$('<input class="passwordViewer" type="text" />').attr("value",d).insertAfter(c)}else{b.val("")}});a.siblings("span").mouseup(function(){var c=$(this);var b=c.siblings("input");if(b.attr("type").toLowerCase()=="password"){c.siblings("input.passwordViewer").remove();b.show()}})});